mapper.insert:
INSERT INTO daedong.board
(g_no, b_place, b_title, b_hash, b_contents, b_writetime, b_delete, b_flat, b_good)
VALUES(#{bGNo.gNo}, #{bPlace}, #{bTitle},#{bHash},#{bContents}, now(), 0, 0, 0);

mapper.update:
UPDATE daedong.board
SET g_no=#{bGNo.gNo}, b_title=#{bTitle}, b_place=#{bPlace}, b_hash=#{bHash}, b_contents=#{bContents}, b_writetime=#{bWritetime}, b_delete=#{bDelete}, b_flat=#{bFlag}, b_good=#{bGood}
WHERE b_no=#{bNo};
